				method: 'GET',
				headers: {
					"x-rapidapi-host": "us-restaurant-menus.p.rapidapi.com",
					"x-rapidapi-key": "68354a4803mshda8c6847c4360bfp157080jsn67f5a244a15c"
				}
			})
			const menuJson = await menu.json();
			return menuJson;

		} catch(err) {
			console.log(err, 'getMenu error');
			return err;
		}
	}

	componentDidMount() {
		this.getMenu()
			.then((data) => {
				console.log(data, 'data from componentDidMount');
				this.setState({
					menu: data
				})
			})
	}

	handleChange = (e) => {
		this.setState({
			[e.currentTarget.name]: e.currentTarget.value
		})
	}

