import React, { Component } from 'react';
import MenuList from '../MenuList';

class Menu extends Component {
	constructor() {
		super();

		this.state = {
			items: [],
			isLoaded: false
		}
	}

	getMenu = async() => {

		try {
			const menu = await fetch("https://developers.zomato.com/api/v2.1/search?entity_id=305&entity_type=city&q=Olive%20Garden", {
				method: 'GET',
				headers: {
					'Content-Type': 'application/json',
            		'user-key': 'b0639e223ba019c8af3a7232ba72d6fa'
				}
			})
			const menuJson = await menu.json();
			return menuJson;

		} catch(err) {
			console.log(err, 'getMenu error');
			return err;
		}
	}

	componentDidMount() {
		this.getMenu()
			.then((data) => {
				console.log(data, 'data from componentDidMount');
				this.setState({
					menu: data
				})
			})
	}

	handleChange = (e) => {
		this.setState({
			[e.currentTarget.name]: e.currentTarget.value
		})
	}

	render() {

		let { isLoaded, items } = this.state;

		if(!isLoaded) {
			return<div>Loading...</div>;
		}

		else {
		return (
			<main>
			<div onSubmit={this.getRestaurantId} class="ui action input">
  				<input type="text" placeholder="Search..." name='restaurant' onChange={this.handleChange} />
  				<button type="submit" class="ui button">Search</button>
			</div>
			<div>
				data loaded
			</div>
			</main>
			)
	}
	}
}

export default Menu;
