import React, { Component } from 'react';
import { Grid } from 'semantic-ui-react';
import MenuContainer from '../MenuContainer';
// import CitySearch from '../CitySearch';

class Profile extends Component {
	constructor(props) {
		super(props);

		this.state = {
			username: '',
			city: '',
			search: ''
		}
	}

	handleSearchChange = () => {
		this.setState({
			search: this.search.value
		})
	}

	handleCityChange = () => {
		this.setState({
			city: this.city.value
		})
	}

	render() {
		// console.log(this.state.search, "this.state.search in profile render");
		return (
			<Grid stackable columns={3} padded className='Profile'>
				<Grid.Row>
					<Grid.Column width={4}>
						Welcome {this.props.userInfo.username}!<br/>Please enter restaurant name:
					</Grid.Column>
					<Grid.Column width={4}>
						<div class="ui action input">
  							<input type="text" placeholder="Search City" name='restaurant' ref={input => this.city = input} />
  							<button onClick={this.handleCityChange} type="submit" class="ui button">Search</button>
						</div>
					</Grid.Column>
					<Grid.Column width={4}>
						<div class="ui action input">
  							<input type="text" placeholder="Search..." name='restaurant' ref={input => this.search = input} />
  							<button onClick={this.handleSearchChange} type="submit" class="ui button">Search</button>
						</div>
					</Grid.Column>
				</Grid.Row>
				<Grid.Row>
					<Grid.Column width={4}>
						<MenuContainer search={this.state.search} />
					</Grid.Column>
					<Grid.Column width={4}>

					</Grid.Column>
				</Grid.Row>
			</Grid>
			)
	}
}

export default Profile;
