import React, { Component } from 'react';


class MenuContainer extends Component {
	constructor() {
		super();

		this.state = {
			restaurants: [],
			isLoaded: false,
			favorites: []
		}
	}

	setRestaurants = () => {
		this.setState({
			restaurants: this.props.restaurants
		})
	}

	handleCheckboxChange = (e) => {

		this.setState({
			favorites: [...this.state.favorites, e.target.name]
		}, () => {
			console.log(this.state.favorites, 'this.state.favorites in handleCheckboxChange');
		})
	}

	componentDidMount() {
		this.setRestaurants();
		this.setState({
			isLoaded: true
		})

	}

	render() {

		let { isLoaded, restaurants } = this.state;

		if(!isLoaded) {
			return<div>Loading...</div>;
		}

		else {
		return (
			<main>

				<tr >
					{this.props.restaurants.map(restaurant => (
					<div>
						<a class="ui card" href={restaurant.restaurant.menu_url} target="_blank">
  							<div style={{ backgroundColor: 'aquamarine' }} class="content">
							    <div class="header">{restaurant.restaurant.name}></div>
							    <div class="meta">{restaurant.restaurant.cuisines}</div>
							    <div class="description">
							      {restaurant.restaurant.location.address}
								</div>
								<input onChange={this.handleCheckboxChange} name={restaurant.restaurant.name} type='checkbox'/>Add to Favorites						
  							</div>
						</a>
					</div>
					))}
				</tr>
			</main>
			)
		}
	}
}

export default MenuContainer;
