import React, { Component } from 'react';


class MenuContainer extends Component {
	constructor() {
		super();

		this.state = {
			restaurants: [],
			isLoaded: false,
			favorites: [],
			id: '',
			name: '',
			cuisines: '',
			address: '',
			menu_url: ''

		}
	}

	setRestaurants = () => {
		this.setState({
			restaurants: this.props.restaurants
		})
	}

	handleCheckboxChange = async (e, favorite) => {

		this.setState({
			name: e.currentTarget.name,
			id: e.currentTarget.id,
			cuisines: e.currentTarget.cuisines,
			address: e.currentTarget.address,
			menu_url: e.currentTarget.menu_url
		}, () => {
			console.log(this.state.name, this.state.id, 'this.state.favorites in handleCheckboxChange');
		})
		try {
			// console.log("SOMETHING");
			const addFavorite = await fetch('http://localhost:8000/api/v1/', {
				method: "POST",
				body: favorite,
				credentials: 'include',
				headers: {
					'Content-Type': 'application/json'
				}
			})
			console.log(addFavorite.status, "addFavorite status before status");
			if(addFavorite.status !==200) {
				throw Error('404 from server')
				console.log(addFavorite.status, 'addFavorite.status after');
			}
			const addFavoriteResponse = await addFavorite.json();
			console.log(addFavoriteResponse.data, "Favorites DATA");
			this.setState({
				faovrites: [...addFavorite.data]
			})
		} catch(err) {
			console.log(err, 'addFavorite error');
			return err
		}
	}

	getFavorites = async() => {
		try {
			console.log(this.props, 'props in get');
			const responseGetFavorites = await fetch('http://localhost:8000/api/v1/' + this.props.userInfo.id)

			console.log(responseGetFavorites, 'responseGetFavorites');
			const favoritesResponse = await responseGetFavorites.json();
			console.log(await favoritesResponse, '<-favoritesResponse');
			if(favoritesResponse.status.code !== 200) {
				throw Error('404 from server')
			}
			this.setState({
				favorites: favoritesResponse.data
			})
		} catch(err) {
			console.log(err, 'err from getFavorites');
			return err
		}
	}

	componentDidMount() {
		this.setRestaurants();
		this.setState({
			isLoaded: true
		})

	}

	render() {

		let { isLoaded, restaurants } = this.state;

		// if(!isLoaded) {
		// 	return<div>Loading...</div>;
		// }

		// else {
		return (
			<main>

				<tr >
					{this.props.restaurants.map(restaurant => (
					<div>
						<a className="ui card" href={restaurant.restaurant.menu_url} target="_blank" name='menu_url'>
  							<div style={{ backgroundColor: 'aquamarine' }} className="content">
							    <div className="header" name='name'>{restaurant.restaurant.name}</div>
							    <div className="meta" name='cuisines'>{restaurant.restaurant.cuisines}</div>

							    <div className="description" name='address'>{restaurant.restaurant.location.address}</div>
							    <div style={{ opacity: ".01" }} name='id' className="description">{restaurant.restaurant.id}</div>
								<input onChange={this.handleCheckboxChange} type='checkbox'
									id={restaurant.restaurant.id} 
									name={restaurant.restaurant.name} 
									cuisines={restaurant.restaurant.cuisines} 
									address={restaurant.restaurant.location.address} 
									menu_url={restaurant.restaurant.menu_url}
								/>Add to Favorites						
  							</div>
						</a>
					</div>
					))}
				</tr>
			</main>
			)
		// }
	}
}

export default MenuContainer;
