import React, { Component } from 'react';

class Menu extends Component {
	constructor() {
		super();

		this.state = {
			menu: {
				item: '',
				description: '',
				price: ''
			}
		}
	}

	getMenu = async() => {
		try {
			const menu = await fetch('https://us-restaurant-menus.p.rapidapi.com/restaurant/' +  + '/menuitems', {
				method: 'GET',
				headers: {
					"x-rapidapi-host": "us-restaurant-menus.p.rapidapi.com",
					"x-rapidapi-key": "68354a4803mshda8c6847c4360bfp157080jsn67f5a244a15c"
				}
			})
			const menuJson = await menu.json();
			return menuJson;

		} catch(err) {
			console.log(err, 'getMenu error');
			return err;
		}
	}

	componentDidMount() {
		this.getMenu()
			.then((data) => {
				console.log(data, 'data from componentDidMount');
				this.setState({
					menu: data
				})
			})
	}

	render() {
		return (
			<div onSubmit={this.getMenu} class="ui action input">
  				<input type="text" placeholder="Search..." />
  				<button type="submit" class="ui button">Search</button>
			</div>
			)
	}
}

export default Menu;
