import React, { Component } from 'react';
import MenuList from '../MenuList';
import { Grid } from 'semantic-ui-react';

class Menu extends Component {
	constructor(props) {
		super(props);

		this.state = {
			restaurants: [],
			isLoaded: false,
			search: ''
		}
	}

	getMenu = async() => {
		console.log(this.props.search, 'this.props.search in getMenu');
		try {
			const menu = await fetch(`https://developers.zomato.com/api/v2.1/search?entity_id=305&entity_type=city&q=${this.state.search}`, {
				method: 'GET',
				headers: {
					'Content-Type': 'application/json',
            		'user-key': 'b0639e223ba019c8af3a7232ba72d6fa'
				}
			})
			const menuJson = await menu.json();
			return menuJson;

		} catch(err) {
			console.log(err, 'getMenu error');
			return err;
		}
	}

	componentDidMount() {
		this.getMenu()
			.then((data) => {
				// console.log(data, 'data from componentDidMount');
				this.setState({
					restaurants: data,
					isLoaded: true
				})
				// console.log(this.state, "this.state");
			})
	}

	handleChange = () => {
		this.setState({
			search: this.search.value
		})
		.this.componentDidMount()
	}

	render(props) {

		let { isLoaded, restaurants } = this.state;
		// console.log(restaurants.restaurants, 'restaurants in Render');
		console.log(this.props.search, 'this.props.search in Render');
		if(!isLoaded) {
			return<div>Loading...</div>;
		}

		else {
		return (
			<main>
				<Grid stackable columns={2} padded className='Profile'>
					<Grid.Row>
						<Grid.Column width={4}>
							<div class="ui action input">
	  							<input type="text" placeholder="Search..." name='restaurant' ref={input => this.search = input} />
	  							<button onClick={this.handleChange} type="submit" class="ui button">Search</button>
							</div>
						</Grid.Column>
						<Grid.Column width={4}>

						</Grid.Column>
					</Grid.Row>
					<Grid.Row>
						<Grid.Column width={4}>
							<tr>
								{restaurants.restaurants.map(restaurant => (
									<a class="ui card" href={restaurant.restaurant.menu_url} target="_blank">
  										<div class="content">
							    			<div class="header">{restaurant.restaurant.name}</div>
							    			<div class="meta">{restaurant.restaurant.cuisines}</div>
							    			<div class="description">{restaurant.restaurant.location.address}</div>
  										</div>
									</a>
								))}	
							</tr>
						</Grid.Column>
						<Grid.Column width={8}>

						</Grid.Column>
					</Grid.Row>
				</Grid>

			</main>
			)
		}
	}
}

export default Menu;
